mapper.insert:
INSERT INTO map_content (
	 map_no
	 , user_no
	 , content
	 , regdate
	 ) 
VALUES (
	#{map_no}
	, #{user_no}
	, #{content}
	, NOW()
)

	SELECT LAST_INSERT_ID()

INSERT INTO map_file (
	filename_org
	, filename_real
	, map_no
	, content_no
	, user_no
	, group_no
) VALUES (
 	#{filename_org}
	, #{filename_real}
	, #{map_no}
	, #{content_no}
	, #{user_no}
	, #{group_no}
)

INSERT INTO map (
	px
	, py
	, name
	, displayName
	, address
	, creationTime
	, user_no
	, marker
	, group_no
)
VALUES (
	#{px}
	, #{py}
	, #{displayName}
	, #{displayName}
	, #{address}
	, NOW()
	, #{user_no}
	, #{marker}
	, #{group_no}
)

	SELECT LAST_INSERT_ID()
